// src/components/InfoEnergia.jsx
import React, { useState, useEffect } from 'react';
import './InfoEnergia.css'; // Para estilos adicionales

const InfoEnergia = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div>
      <div className={`hero-image ${scrolled ? "scrolled" : ""}`}>
        <img src="/img/EnergiaRenovable.png" alt="Imagen energía renovable" />
      </div>
      <div className="container mt-5">
        <h1>Bienvenido a Energía Renovable Talento Tech</h1>
        <p>Aquí puedes explorar gráficas, estimaciones y más.</p>
        {/* Agrega más contenido si deseas */}
      </div>
    </div>
  );
};

export default InfoEnergia;
