import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function SelectDesdeLista() {
  const [opciones, setOpciones] = useState([]);
  const [seleccionado, setSeleccionado] = useState('');
  const [gifSrc, setGifSrc] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8000/paises')
      .then((response) => {
        setOpciones(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener los datos:', error);
      });
  }, []);

  const manejarCambio = (event) => {
    const pais = event.target.value;
    setSeleccionado(pais);
    if (pais) {
      setGifSrc(`http://localhost:8000/graphics/${pais}_renewables.gif`);
    } else {
      setGifSrc(null);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Selecciona un país</h2>
      <select className="form-select mb-3" onChange={manejarCambio} value={seleccionado}>
        <option value="">-- Elige un país --</option>
        {opciones.map((pais, i) => (
          <option key={i} value={pais}>{pais}</option>
        ))}
      </select>

      {gifSrc && (
        <div className="text-center mt-4">
          <img src={gifSrc} alt={`Gráfico de ${seleccionado}`} className="img-fluid" />
        </div>
      )}
    </div>
  );
}
