import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Graficos = () => {
  const [graficos, setGraficos] = useState([]);
  const [cargando, setCargando] = useState(true); // Nuevo estado de carga

  useEffect(() => {
    axios.get('http://localhost:8000/generar-graficos')
      .then(() => {
        setGraficos([
          {
            titulo: 'Producción de Energía Renovable por Fuente',
            url: 'http://localhost:8000/graphics/grafico_barras_renovables.gif'
          },
          {
            titulo: 'Participación de Energías Renovables',
            url: 'http://localhost:8000/graphics/grafico_torta_renovables.gif'
          },
          {
            titulo: 'Tendencia en la Capacidad Instalada',
            url: 'http://localhost:8000/graphics/grafico_lineas_capacidad.gif'
          },
          {
            titulo: 'Comparación entre Consumo de Energía Renovable y Convencional',
            url: 'http://localhost:8000/graphics/grafico_area_comparativo.gif'
          }
        ]);
      })
      .catch(error => {
        console.error("Error generando los gráficos:", error);
      })
      .finally(() => {
        setCargando(false); // Marcar como cargado, sea éxito o error
      });
  }, []);

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center text-green-700">
        Visualización de Gráficos de Energía Renovable
      </h1>

      {cargando ? (
        <div className="text-center text-gray-600 text-lg">Cargando gráficos...</div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {graficos.map((grafico, index) => (
            <div key={index} className="bg-white rounded-xl shadow-md p-6">
              <h2 className="text-lg font-semibold mb-2">{grafico.titulo}</h2>
              <img
                src={grafico.url}
                alt={grafico.titulo}
                className="w-full rounded-md border"
              />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Graficos;
