import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Graficos = () => {
  const [gifGenerado, setGifGenerado] = useState(null);

  useEffect(() => {
    // Llamada para generar los gráficos al montar el componente
    axios.get('http://localhost:8000/generar-graficos')
      .then(() => {
        // Esperamos que el backend genere y guarde el archivo en /graphics
        setGifGenerado('http://localhost:8000/graphics/grafico_torta_renovables.gif');
      })
      .catch(error => {
        console.error("Error generando el gráfico:", error);
      });
  }, []);

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center text-green-700">
        Visualización de Gráficos de Energía Renovable
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {gifGenerado && (
          <div className="bg-white rounded-xl shadow-md p-6">
            <h2 className="text-lg font-semibold mb-2">Participación de Energías Renovables</h2>
            <img
              src={gifGenerado}
              alt="Gráfico de energías renovables"
              className="w-full rounded-md border"
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default Graficos;
