import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function SelectDesdeLista() {
  const [opciones, setOpciones] = useState([]);
  const [seleccionado, setSeleccionado] = useState('');
  const [gifSrc, setGifSrc] = useState(null);
  const [cargando, setCargando] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:8000/paises')
      .then((response) => {
        setOpciones(response.data);
      })
      .catch((error) => {
        console.error('Error al obtener los datos:', error);
      });
  }, []);

  const manejarCambio = async (event) => {
    const pais = event.target.value;
    setSeleccionado(pais);
    setCargando(true);
    setGifSrc(null);

    try {
      await axios.get(`http://localhost:8000/graficos/${pais}`);
      // Esperamos un poco por seguridad (opcional, puedes ajustar o quitar)
      setTimeout(() => {
        setGifSrc(`http://localhost:8000/graphics/${pais}_renewables.gif`);
        setCargando(false);
      }, 500); // 500ms de espera por si hay caché sin sincronizar
    } catch (error) {
      console.error('Error al generar el gráfico:', error);
      setCargando(false);
    }
  }
}
